#!/bin/bash#SBATCH -p test#SBATCH -o block_LD.out#SBATCH -t 0-04:00#SBATCH --mem 1Gblock_dir=/n/holystore01/LABS/xlin/Lab/tonychen/L0LearnSum_package/Blocksmodule load plinkfile=${1}for chrom in {1..22}; don_blocks=$(ls ${block_dir}/Range/chr_${chrom}_* | wc -l)echo $chrom $n_blocksfor ((block=1; block<=$n_blocks; block++)); doecho -n $block ..# calculate LD for each blockplink --silent --bfile ${file} \--extract range ${block_dir}/Range/chr_${chrom}_block_${block}.range \--r square \--allow-no-sex \--silent \--write-snplist \--out ${block_dir}/LD/chr_${chrom}_block_${block}doneecho donerm ${block_dir}/LD/*log ${block_dir}/LD/*nosex